import React from 'react';
import './SignalCard.css';

const SignalCard = ({ signal }) => {
  const isProfit = signal.variation > 0;
  const isBuy = signal.type.toUpperCase() === 'COMPRA';
  
  return (
    <div className={`signal-card ${isBuy ? 'buy' : 'sell'}`}>
      <div className="card-header">
        <div className="symbol-container">
          <h2 className="symbol">{signal.symbol}</h2>
          <div className="type-label">{signal.type}</div>
        </div>
        <div className="premium-tag">Sinais Premium</div>
      </div>

      <div className="price-details">
        <div className="price-row">
          <span className="price-label">ðŸ’° Entrada:</span>
          <span className="price-value">${signal.entry_price}</span>
        </div>
        <div className="price-row">
          <span className="price-label">ðŸŽ¯ Alvo:</span>
          <span className="price-value">${signal.target_price}</span>
        </div>
        <div className="variation-row">
          <span className={`variation ${isProfit ? 'profit' : 'loss'}`}>
            ({isProfit ? '+' : ''}{signal.variation?.toFixed(1)}%)
          </span>
        </div>
      </div>

      <div className="timestamp">
        {new Date(signal.timestamp).toLocaleString()}
      </div>
    </div>
  );
};

export default SignalCard;
