import React, { useState, useEffect } from 'react';
import { getSignals } from './services/signalsService';
import SignalsContainer from './components/SignalsContainer';
import MainLayout from './components/layout/MainLayout';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

function App() {
  const [signals, setSignals] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSignals = async () => {
      try {
        const data = await getSignals();
        setSignals(data);
      } catch (error) {
        console.error('Erro ao carregar sinais:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchSignals();
    const interval = setInterval(fetchSignals, 60000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return <div className="loading">Carregando sinais...</div>;
  }

  return (
    <MainLayout>
      <div className="app">
        <header className="app-header">
          <h1>KryptoN Trading Bot</h1>
        </header>
        <main>
          <SignalsContainer signals={signals} />
        </main>
      </div>
    </MainLayout>
  );
}

export default App;
