import React from 'react';
import './SignalCard.css';

const SignalCard = ({ signal }) => {
  const isCompra = signal.type.toUpperCase() === 'COMPRA';
  const typeColor = isCompra ? 'compra' : 'venda';
  const variationValue = signal.variation > 0 ? `+${signal.variation}%` : `${signal.variation}%`;

  return (
    <div className={`signal-card ${typeColor}`}>
      <div className="card-header">
        <div className="symbol">{signal.symbol}</div>
        <div className={`type-label ${typeColor}`}>
          {signal.type}
        </div>
      </div>

      <div className="premium-tag">Sinais Premium</div>
      
      <div className="price-info">
        <div className="price-row">
          <span className="price-label">ðŸ’° Entrada:</span>
          <span className="price-value">${signal.entry_price}</span>
        </div>
        
        <div className="price-row">
          <span className="price-label">ðŸŽ¯ Alvo:</span>
          <span className="price-value">${signal.target_price}</span>
          <span className={`variation ${typeColor}`}>{variationValue}</span>
        </div>
      </div>

      <div className="timestamp">
        {signal.timestamp || 'Invalid Date'}
      </div>
    </div>
  );
};

export default SignalCard;
