import React from 'react';
import './SignalCard.css';

const SignalCard = ({ signal }) => {
  const isProfit = signal.variation > 0;
  
  return (
    <div className={`signal-card ${signal.type.toLowerCase()}`}>
      <div className="card-header">
        <div className="symbol">{signal.symbol}</div>
        <div className={`score ${signal.quality_score >= 90 ? 'high' : 'medium'}`}>
          {signal.quality_score} pontos
        </div>
      </div>
      
      <div className="card-type">
        {signal.type} {signal.trend_timeframe}
      </div>
      
      <div className="price-info">
        <div className="entry">
          <span>Entrada</span>
          <span>${signal.entry_price}</span>
        </div>
        <div className="target">
          <span>Alvo</span>
          <span>${signal.target_price}</span>
        </div>
      </div>
      
      <div className={`variation ${isProfit ? 'profit' : 'loss'}`}>
        {signal.variation ? `${signal.variation.toFixed(2)}%` : '0.00%'}
      </div>
      
      <div className="quality">
        {signal.signal_class}
      </div>
    </div>
  );
};

export default SignalCard;
